// let tmax  = flow.get('duration') || 90   // calculation period (days)
let dt = 0.1 // computation step (days)
let gamma = 1 / D
let beta = R0 * gamma
let x = []
x[0] = {"t":0, "s":0.999, "i":0.001, "r":0} // initial values
for (t = 1; t <= tmax; t++) { // point per day
